mapper.select:
select u.*, ur.tabinfo AS tabInfo
 from xc_mng_users AS u, xc_mng_user_role AS ur
where u.role = ur.tid and u.username = #{userName} and u.password = #{password}

mapper.update:
update xc_mng_users
set NickName=#{nickName},phone=#{phone}
where userId=#{userId}

update xc_mng_users
set UserPassword=#{1}
where userId=#{0}
